    public static ArrayList<Cats> getListData(){
        Cats cats = null;
        ArrayList<Cats> list = new ArrayList<>();
        for (int i=0; i < data.length; i++){
            cats = new Cats();
            cats.setName(data[i][0]);
            cats.setDesc(data[i][1]);
            cats.setPhoto(data[i][2]);

            list.add(cats);
        }
        return list;
    }
